    private void btnAllActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAllActionPerformed
        // TODO add your handling code here:
        //showing all the employees
        String[] data=new String[4];
        DefaultTableModel tblModel=(DefaultTableModel)jTable1.getModel();
        tblModel.setRowCount(0);//setting rows 0 when starting
        for(Department department:Manager.departmentList){
            for(Designation designation:department.getDesignations()){
                for(Employee employee:designation.getEmployees()){
                    data[0]=department.getDepartmentName();//col 1
                    data[1]=designation.getDesignationName();//col 2
                    data[2]=employee.getName();//col3
                    data[3]=Integer.toString(employee.getEpfNum());//col4
                    tblModel.addRow(data);//adding row to the table
                }
            }
        }
        
    }//GEN-LAST:event_btnAllActionPerformed

    private void btnDesignationActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDesignationActionPerformed
        // TODO add your handling code here:
        //search employees from the designation
        String designationName=tfSearch.getText();//getting the name of the designation
        String[] data=new String[4];
        DefaultTableModel tblModel=(DefaultTableModel)jTable1.getModel();
        tblModel.setRowCount(0);//setting rows 0 when starting
        for(Department department:Manager.departmentList){
            for(Designation designation:department.getDesignations()){
                for(Employee employee:designation.getEmployees()){
                    if(designation.getDesignationName().equals(designationName)){//checking the designation
                        data[0]=department.getDepartmentName();//col1
                        data[1]=designation.getDesignationName();//col2
                        data[2]=employee.getName();//col2
                        data[3]=Integer.toString(employee.getEpfNum());//col3
                        tblModel.addRow(data);//adding the row to the table
                    }
                }
            }
        }
        
    }//GEN-LAST:event_btnDesignationActionPerformed

    private void btnEpfNumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEpfNumActionPerformed
        // TODO add your handling code here:
        //showing employees have given epf number
        String epf=tfSearch.getText();//getting the epf number
        String[] data=new String[4];
        DefaultTableModel tblModel=(DefaultTableModel)jTable1.getModel();
        tblModel.setRowCount(0);//setting rows 0 when starting
        for(Department department:Manager.departmentList){
            for(Designation designation:department.getDesignations()){
                for(Employee employee:designation.getEmployees()){
                    if(employee.getEpfNum()==Integer.valueOf(epf)){//checking the epf number 
                        data[0]=department.getDepartmentName();//col1
                        data[1]=designation.getDesignationName();//col2
                        data[2]=employee.getName();//col3
                        data[3]=Integer.toString(employee.getEpfNum());//col4
                        tblModel.addRow(data);//adding the row for the table
                    }
                }
            }
        }
    }//GEN-LAST:event_btnEpfNumActionPerformed

