    private void btnNameActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNameActionPerformed
        // TODO add your handling code here:
         //showing employees have given name
        String name=tfSearch.getText();//get input from textfield
        String[] data=new String[4];
        DefaultTableModel tblModel=(DefaultTableModel)jTable1.getModel();
        tblModel.setRowCount(0);//setting rows 0 when starting
        for(Department department:Manager.departmentList){
            for(Designation designation:department.getDesignations()){
                for(Employee employee:designation.getEmployees()){
                    if(employee.getName().equals(name)){//checking the name
                        data[0]=department.getDepartmentName();//col1
                        data[1]=designation.getDesignationName();//col2
                        data[2]=employee.getName();//col3
                        data[3]=Integer.toString(employee.getEpfNum());//col4
                        tblModel.addRow(data);//adding row to the table
                    }
                }
            }
        }
    }//GEN-LAST:event_btnNameActionPerformed

